<script>
// Eyebrow dinámico: español = .ES, inglés = .AI
(function() {
  const el = document.getElementById('brandEyebrow');
  const isSpanish = (document.documentElement.lang || '').startsWith('es');
  const host = (location.hostname || '').toLowerCase();
  const useES = isSpanish || host.endsWith('.es');
  el.textContent = useES ? 'NEXTGEN PM.ES' : 'NEXTGEN PM.AI®';
})();

// Scroll reveal: Intersection Observer
(function() {
  const hero = document.querySelector('.hero');
  
  const observerOptions = {
    threshold: 0.1,
    rootMargin: '0px 0px -50px 0px'
  };

  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.classList.add('is-visible');
        observer.unobserve(entry.target);
      }
    });
  }, observerOptions);

  observer.observe(hero);
})();

// Canvas de partículas MEJORADO
(function() {
  const hero = document.querySelector('.hero');
  const canvas = document.getElementById('particles');
  const ctx = canvas.getContext('2d');

  function resizeCanvas() {
    canvas.width = window.innerWidth;
    canvas.height = hero.offsetHeight;
  }

  let particles = [];

  function initParticles() {
    const count = window.innerWidth < 768 ? 48 : 96;
    particles = Array.from({ length: count }, () => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      r: 1.8 + Math.random() * 2.5,
      dx: (Math.random() - 0.5) * 0.5,
      dy: (Math.random() - 0.5) * 0.5,
      tw: Math.random() * Math.PI * 2,
      opacity: 0.75 + Math.random() * 0.25
    }));
  }

  function draw() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Conexiones mejoradas
    for (let i = 0; i < particles.length; i++) {
      for (let j = i + 1; j < particles.length; j++) {
        const dx = particles[i].x - particles[j].x;
        const dy = particles[i].y - particles[j].y;
        const dist = Math.sqrt(dx * dx + dy * dy);
        if (dist < 150) {
          const alpha = (1 - dist / 150) * 0.15;
          ctx.strokeStyle = `rgba(255,255,255,${alpha})`;
          ctx.lineWidth = 1.2;
          ctx.beginPath();
          ctx.moveTo(particles[i].x, particles[i].y);
          ctx.lineTo(particles[j].x, particles[j].y);
          ctx.stroke();
        }
      }
    }

    // Partículas con twinkle mejorado
    particles.forEach((p) => {
      p.tw += 0.02;
      const twinkle = (Math.sin(p.tw) + 1) / 2;
      const opacity = p.opacity + (twinkle * 0.25);

      ctx.beginPath();
      ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255,255,255,${opacity})`;
      ctx.fill();

      // Glow sutil en partículas grandes
      if (p.r > 2.5) {
        ctx.strokeStyle = `rgba(214,69,216,${opacity * 0.3})`;
        ctx.lineWidth = 0.5;
        ctx.stroke();
      }

      p.x += p.dx;
      p.y += p.dy;
      if (p.x < 0 || p.x > canvas.width) p.dx *= -1;
      if (p.y < 0 || p.y > canvas.height) p.dy *= -1;
    });

    requestAnimationFrame(draw);
  }

  const ro = new ResizeObserver(() => {
    resizeCanvas();
    initParticles();
  });

  ro.observe(hero);
  resizeCanvas();
  initParticles();
  draw();

  window.addEventListener('orientationchange', () => {
    setTimeout(() => {
      resizeCanvas();
      initParticles();
    }, 250);
  });
})();
</script>
