    <script>
        (function() {
            const section = document.querySelector('.siguiente-caso');
            const canvas = document.getElementById('particles');
            const svg = document.getElementById('neural-svg');
            const ctx = canvas.getContext('2d');

            function resizeCanvas() {
                canvas.width = window.innerWidth;
                canvas.height = section.offsetHeight;
                svg.setAttribute('width', window.innerWidth);
                svg.setAttribute('height', section.offsetHeight);
            }

            let particles = [];

            function initParticles() {
                const count = window.innerWidth < 768 ? 45 : 100;
                particles = Array.from({ length: count }, () => ({
                    x: Math.random() * canvas.width,
                    y: Math.random() * canvas.height,
                    r: 1.8 + Math.random() * 2.8,
                    dx: (Math.random() - 0.5) * 0.5,
                    dy: (Math.random() - 0.5) * 0.5,
                    tw: Math.random() * Math.PI * 2,
                    opacity: 0.75 + Math.random() * 0.25
                }));
            }

            function drawSvgNeurons() {
                svg.innerHTML = '';

                for (let i = 0; i < particles.length; i++) {
                    for (let j = i + 1; j < particles.length; j++) {
                        const dx = particles[i].x - particles[j].x;
                        const dy = particles[i].y - particles[j].y;
                        const dist = Math.sqrt(dx * dx + dy * dy);
                        if (dist < 160) {
                            const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
                            line.setAttribute('x1', particles[i].x);
                            line.setAttribute('y1', particles[i].y);
                            line.setAttribute('x2', particles[j].x);
                            line.setAttribute('y2', particles[j].y);
                            line.setAttribute('class', 'neural-line');
                            svg.appendChild(line);
                        }
                    }
                }

                particles.forEach((p) => {
                    const glow = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
                    glow.setAttribute('cx', p.x);
                    glow.setAttribute('cy', p.y);
                    glow.setAttribute('r', p.r + 1.2);
                    glow.setAttribute('class', 'neural-dot-glow');
                    svg.appendChild(glow);

                    const dot = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
                    dot.setAttribute('cx', p.x);
                    dot.setAttribute('cy', p.y);
                    dot.setAttribute('r', p.r);
                    dot.setAttribute('class', 'neural-dot');
                    svg.appendChild(dot);
                });
            }

            function draw() {
                ctx.clearRect(0, 0, canvas.width, canvas.height);

                for (let i = 0; i < particles.length; i++) {
                    for (let j = i + 1; j < particles.length; j++) {
                        const dx = particles[i].x - particles[j].x;
                        const dy = particles[i].y - particles[j].y;
                        const dist = Math.sqrt(dx * dx + dy * dy);
                        if (dist < 160) {
                            const alpha = (1 - dist / 160) * 0.15;
                            ctx.strokeStyle = `rgba(255,255,255,${alpha})`;
                            ctx.lineWidth = 1.2;
                            ctx.beginPath();
                            ctx.moveTo(particles[i].x, particles[i].y);
                            ctx.lineTo(particles[j].x, particles[j].y);
                            ctx.stroke();
                        }
                    }
                }

                particles.forEach((p) => {
                    p.tw += 0.02;
                    const twinkle = (Math.sin(p.tw) + 1) / 2;
                    const opacity = p.opacity + (twinkle * 0.28);

                    ctx.beginPath();
                    ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
                    ctx.fillStyle = `rgba(255,255,255,${opacity})`;
                    ctx.fill();

                    if (p.r > 2.5) {
                        ctx.strokeStyle = `rgba(214,69,216,${opacity * 0.32})`;
                        ctx.lineWidth = 0.6;
                        ctx.stroke();
                    }

                    p.x += p.dx;
                    p.y += p.dy;
                    if (p.x < 0 || p.x > canvas.width) p.dx *= -1;
                    if (p.y < 0 || p.y > canvas.height) p.dy *= -1;
                });

                drawSvgNeurons();
                requestAnimationFrame(draw);
            }

            const ro = new ResizeObserver(() => {
                resizeCanvas();
                initParticles();
            });

            ro.observe(section);
            resizeCanvas();
            initParticles();
            draw();

            window.addEventListener('orientationchange', () => {
                setTimeout(() => {
                    resizeCanvas();
                    initParticles();
                }, 250);
            });
        })();
    </script>
</body>
</html>
