<script>
  // Canvas de partículas neuronales - robusto y seguro para Elementor
  (function() {
    var hero = document.querySelector('.hero-home');
    var canvas = document.getElementById('particles-home');
    if (!hero || !canvas) return;

    var ctx = canvas.getContext('2d', { alpha: true });
    if (!ctx) return;

    var particles = [];
    var rafId = null;

    function resizeCanvas() {
      canvas.width = hero.offsetWidth;
      canvas.height = hero.offsetHeight;
    }

    function initParticles() {
      var count = window.innerWidth < 768 ? 40 : 100;
      particles = Array.from({ length: count }, function() {
        return {
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          r: 2 + Math.random() * 2.5,
          dx: (Math.random() - 0.5) * 0.6,
          dy: (Math.random() - 0.5) * 0.6,
          tw: Math.random() * Math.PI * 2
        };
      });
    }

    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      for (var i = 0; i < particles.length; i++) {
        for (var j = i + 1; j < particles.length; j++) {
          var dx = particles[i].x - particles[j].x;
          var dy = particles[i].y - particles[j].y;
          var dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < 180) {
            var alpha = (1 - dist / 180) * 0.25;
            ctx.strokeStyle = 'rgba(255, 255, 255, ' + alpha + ')';
            ctx.lineWidth = 2.5;
            ctx.beginPath();
            ctx.moveTo(particles[i].x, particles[i].y);
            ctx.lineTo(particles[j].x, particles[j].y);
            ctx.stroke();
          }
        }
      }

      particles.forEach(function(p) {
        p.tw += 0.02;
        var twinkle = (Math.sin(p.tw) + 1) / 2;

        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(255, 255, 255, ' + (0.65 + twinkle * 0.35) + ')';
        ctx.fill();

        p.x += p.dx;
        p.y += p.dy;

        if (p.x < 0 || p.x > canvas.width) p.dx *= -1;
        if (p.y < 0 || p.y > canvas.height) p.dy *= -1;
      });

      rafId = window.requestAnimationFrame(draw);
    }

    function boot() {
      resizeCanvas();
      initParticles();
      if (rafId) cancelAnimationFrame(rafId);
      draw();
    }

    if ('ResizeObserver' in window) {
      var ro = new ResizeObserver(function() {
        boot();
      });
      ro.observe(hero);
    } else {
      window.addEventListener('resize', function() {
        boot();
      }, { passive: true });
    }

    window.addEventListener('orientationchange', function() {
      setTimeout(function(){ boot(); }, 250);
    });

    boot();
  })();
</script>
