<script>
  (function () {
    var root = document.getElementById('ng-about-block');
    if (!root) return;

    var FA_HREF = 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css';

    function isFaLoaded() {
      var probe = document.createElement('i');
      probe.className = 'fa-solid fa-circle';
      probe.style.position = 'absolute';
      probe.style.left = '-9999px';
      probe.style.fontSize = '32px';
      root.appendChild(probe);
      var fam = window.getComputedStyle(probe).getPropertyValue('font-family') || '';
      root.removeChild(probe);
      return /Font Awesome/i.test(fam);
    }

    function ensureFontAwesomeLoaded(cb) {
      if (isFaLoaded()) return cb && cb();

      var existing = document.querySelector('link[href*="font-awesome"],link[href*="fontawesome"],link[href*="cdnjs.cloudflare.com/ajax/libs/font-awesome"]');
      if (existing) return cb && cb();

      var link = document.createElement('link');
      link.rel = 'stylesheet';
      link.href = FA_HREF;
      link.crossOrigin = 'anonymous';
      link.referrerPolicy = 'no-referrer';
      link.onload = function(){ cb && cb(); };
      link.onerror = function(){ cb && cb(); };
      document.head.appendChild(link);
    }

    function initReveal() {
      var items = root.querySelectorAll('.js-reveal');
      if (!items.length) return;

      if (!('IntersectionObserver' in window)) {
        items.forEach(function (el) { el.classList.add('is-visible'); });
        return;
      }

      var io = new IntersectionObserver(function (entries) {
        entries.forEach(function (entry) {
          if (entry.isIntersecting) {
            entry.target.classList.add('is-visible');
            io.unobserve(entry.target);
          }
        });
      }, { threshold: 0.12, rootMargin: '0px 0px -50px 0px' });

      items.forEach(function (el) { io.observe(el); });
    }

    function boot() {
      ensureFontAwesomeLoaded(initReveal);
    }

    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', boot);
    } else {
      boot();
    }
  })();
</script>
