    <script>
        // Canvas de partículas neuronales - CTA FINAL CON LÍNEAS GRUESAS
        (function() {
            const section = document.querySelector('.section-cta-final');
            const canvas = document.getElementById('particles-cta-final');
            if (!section || !canvas) return;

            const ctx = canvas.getContext('2d', { alpha: true });

            function resizeCanvas() {
                canvas.width = section.offsetWidth;
                canvas.height = section.offsetHeight;
            }

            let particles = [];

            function initParticles() {
                const count = window.innerWidth < 768 ? 40 : 90;
                particles = Array.from({ length: count }, () => ({
                    x: Math.random() * canvas.width,
                    y: Math.random() * canvas.height,
                    r: 2.5 + Math.random() * 3,
                    dx: (Math.random() - 0.5) * 0.5,
                    dy: (Math.random() - 0.5) * 0.5,
                    tw: Math.random() * Math.PI * 2
                }));
            }

            function draw() {
                ctx.clearRect(0, 0, canvas.width, canvas.height);

                // LÍNEAS DE CONEXIÓN - GRUESAS Y ROBUSTAS
                for (let i = 0; i < particles.length; i++) {
                    for (let j = i + 1; j < particles.length; j++) {
                        const dx = particles[i].x - particles[j].x;
                        const dy = particles[i].y - particles[j].y;
                        const dist = Math.sqrt(dx * dx + dy * dy);

                        if (dist < 200) {
                            const alpha = (1 - dist / 200) * 0.25;
                            ctx.strokeStyle = `rgba(255, 255, 255, ${alpha})`;
                            ctx.lineWidth = 3.5;
                            ctx.lineCap = 'round';
                            ctx.lineJoin = 'round';
                            ctx.beginPath();
                            ctx.moveTo(particles[i].x, particles[i].y);
                            ctx.lineTo(particles[j].x, particles[j].y);
                            ctx.stroke();
                        }
                    }
                }

                // PARTÍCULAS CON TWINKLE - MÁS GRANDES
                particles.forEach(p => {
                    p.tw += 0.02;
                    const twinkle = (Math.sin(p.tw) + 1) / 2;

                    ctx.beginPath();
                    ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
                    ctx.fillStyle = `rgba(255, 255, 255, ${0.65 + twinkle * 0.35})`;
                    ctx.fill();

                    p.x += p.dx;
                    p.y += p.dy;

                    if (p.x < 0 || p.x > canvas.width) p.dx *= -1;
                    if (p.y < 0 || p.y > canvas.height) p.dy *= -1;
                });

                requestAnimationFrame(draw);
            }

            const ro = new ResizeObserver(() => {
                resizeCanvas();
                initParticles();
            });

            ro.observe(section);
            resizeCanvas();
            initParticles();
            draw();

            window.addEventListener('orientationchange', () => {
                setTimeout(() => {
                    resizeCanvas();
                    initParticles();
                }, 250);
            });
        })();
    </script>

</body>
