<script>
    // Al cargar la página, leer query params y rellenar formulario
    document.addEventListener('DOMContentLoaded', function() {
        const params = new URLSearchParams(window.location.search);
        
        // Si viene de "Soporte Continuo", capturar datos del plan
        if (params.has('plan')) {
            const plan = params.get('plan');
            const precio = params.get('precio');
            const sla = params.get('sla');
            const horas = params.get('horas');
            
            // Pre-llenar el textarea con info del plan seleccionado
            const mensajeInput = document.getElementById('mensaje');
            if (mensajeInput) {
                const planInfo = `Interesado en plan ${plan.toUpperCase()}: €${precio}/mes, SLA ${sla}, ${horas} horas incluidas.`;
                mensajeInput.value = planInfo;
            }
        }

        // Manejo del formulario (FORMSPREE)
        const form = document.getElementById('contactForm');
        const successMessage = document.getElementById('successMessage');

        form.addEventListener('submit', function(e) {
            e.preventDefault();
            
            const submitBtn = form.querySelector('.form-submit');
            submitBtn.disabled = true;
            submitBtn.textContent = 'Enviando...';

            fetch(form.action, {
                method: 'POST',
                body: new FormData(form),
                headers: {
                    'Accept': 'application/json'
                }
            })
            .then(response => {
                if (response.ok) {
                    successMessage.classList.add('show');
                    form.reset();
                    submitBtn.disabled = false;
                    submitBtn.textContent = 'Enviar solicitud';
                    
                    setTimeout(() => {
                        successMessage.classList.remove('show');
                    }, 5000);
                    
                    successMessage.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                } else {
                    alert('Hubo un error. Intenta de nuevo.');
                    submitBtn.disabled = false;
                    submitBtn.textContent = 'Enviar solicitud';
                }
            })
            .catch(error => {
                alert('Error al enviar el formulario.');
                console.error('Error:', error);
                submitBtn.disabled = false;
                submitBtn.textContent = 'Enviar solicitud';
            });
        });
    });
</script>
